import './App.css';
import React from 'react';
import useWindowSize from 'react-use/lib/useWindowSize'
import Confetti from 'react-confetti'


function App() {
  const {screen, setScreen} = React.useState(({
    width: window.screen.width,
    height: window.screen.height
  }))
  const [turn, setTurn] = React.useState("X")
  const [cells, setCells] = React.useState(Array(9).fill(""))
  const [winner, setWinner] = React.useState(); 

  const combos = {
    across: [[0,1,2],[3,4,5],[6,7,8]],
    down: [[0,3,6],[1,4,7],[2,5,8]],
    diagonal: [[0,4,8],[2,4,6]]
  }

  React.useEffect(()=>setScreen(({...screen, width: window.screen.width, height: window.screen.height})), )

  const handleClick = (num) => {
    if (cells[num] !== "") return; 
    let arr = [...cells];
    if (turn == "X"){
      arr[num] = "X";
      setTurn("O"); 
    }else{
      arr[num] = "O";
      setTurn("X");
    }
    checkWinner(arr); 
    setCells(arr)
  }

  const checkWinner = (arr) => {
    for (let combo in combos) {
      combos[combo].forEach((pattern) => {
        if (arr[pattern[0]] === "" || arr[pattern[1]] === "" || arr[pattern[2]] === "") {
        } else if (arr[pattern[0]] === arr[pattern[1]] && arr[pattern[1]] === arr[pattern[2]]) {
          console.log(
            "Winner!"
          )
          setWinner(arr[pattern[0]]);
        }
      });
    }
  };


  const Cell = ({num}) => {
    const cellValue = cells[num]
    //if cell is empty we give it class cell, otherwise class X or O
    const cellClassName = cellValue ? `cell cell-${cellValue}` : "cell"

    return (
      <td className={cellClassName} onClick={()=>handleClick(num)}>{cellValue}</td>
    )
  }

  const handleReset = () => {
    setWinner()
    setCells(Array(9).fill(""))
  }

  return (
    <main>
      {winner && <Confetti
      width={width}
      height={height}
    />}
    <div className='container'>
      <table>
        <tr>
          <Cell num={0}></Cell>
          <Cell num={1}></Cell>
          <Cell num={2}></Cell>
        </tr>
        <tr>
          <Cell num={3}></Cell>
          <Cell num={4}></Cell>
          <Cell num={5}></Cell>
        </tr>
        <tr>
          <Cell num={6}></Cell>
          <Cell num={7}></Cell>
          <Cell num={8}></Cell>
        </tr>
      </table>
      <button className='reset-button' onClick={handleReset}>Reset</button>
    </div>
    </main>
  );

}

export default App;
